mapper.insert:

insert into t_message (msg_id, msg_from, msg_to, 
  msg_title, msg_content, msg_status
  )
values (#{msgId,jdbcType=INTEGER}, #{msgFrom,jdbcType=VARCHAR}, #{msgTo,jdbcType=VARCHAR}, 
  #{msgTitle,jdbcType=VARCHAR}, #{msgContent,jdbcType=VARCHAR}, #{msgStatus,jdbcType=TINYINT}
  )

mapper.select:

select 

from t_message


    msg_id = #{msgId,jdbcType=INTEGER}


     and msg_from = #{msgFrom,jdbcType=VARCHAR}


     and msg_to = #{msgTo,jdbcType=VARCHAR}


     and msg_title = #{msgTitle,jdbcType=VARCHAR}


     and msg_content = #{msgContent,jdbcType=VARCHAR}


     and msg_status = #{msgStatus,jdbcType=TINYINT}



  order by ${orderByClause}
