mapper.select:

select 

from user


    uid = #{uid,jdbcType=INTEGER}


     and account = #{account,jdbcType=VARCHAR}


     and real_name = #{realName,jdbcType=VARCHAR}


     and nickname = #{nickname,jdbcType=VARCHAR}


     and passwd = #{passwd,jdbcType=VARCHAR}


     and email = #{email,jdbcType=VARCHAR}


     and create_time = #{createTime,jdbcType=TIMESTAMP}


     and create_channel = #{createChannel,jdbcType=TINYINT}


     and last_time = #{lastTime,jdbcType=TIMESTAMP}


     and last_channel = #{lastChannel,jdbcType=TINYINT}


     and last_ip = #{lastIp,jdbcType=VARCHAR}


     and status = #{status,jdbcType=TINYINT}


     and user_type = #{userType,jdbcType=TINYINT}


     and inv_code = #{invCode,jdbcType=VARCHAR}


     and src = #{src,jdbcType=TINYINT}


     and ditch = #{ditch,jdbcType=INTEGER}


     and consume = #{consume,jdbcType=BIT}


     and ower_ship = #{owerShip,jdbcType=VARCHAR}



  order by ${orderByClause}
